import React, { useState, useEffect } from 'react';
import { FaCode, FaCogs, FaUserTie, FaCodeBranch, FaChevronDown, FaChevronRight, FaPlay } from 'react-icons/fa';

const App = () => {
  const [currentView, setCurrentView] = useState('home');
  const [selectedCategory, setSelectedCategory] = useState('dsa');
  const [selectedLanguage, setSelectedLanguage] = useState(null);
  const [videos, setVideos] = useState([]);
  const [selectedVideo, setSelectedVideo] = useState(null);
  const [loading, setLoading] = useState(false);
  const [languagesExpanded, setLanguagesExpanded] = useState(false);

  const categories = {
    dsa: { 
      name: 'Data Structures & Algorithms', 
      icon: FaCode,
      description: 'Master fundamental algorithms and data structures'
    },
    system: { 
      name: 'System Design', 
      icon: FaCogs,
      description: 'Learn to design scalable systems'
    },
    behavioral: { 
      name: 'Behavioral Questions', 
      icon: FaUserTie,
      description: 'Ace your behavioral interviews'
    },
    languages: { 
      name: 'Language-Specific Prep', 
      icon: FaCodeBranch,
      description: 'Deep dive into programming languages'
    }
  };

  const languageSubcategories = {
    python: 'Python',
    javascript: 'JavaScript',
    java: 'Java',
    cpp: 'C++',
    csharp: 'C#',
    go: 'Go',
    rust: 'Rust',
    kotlin: 'Kotlin',
    swift: 'Swift',
    typescript: 'TypeScript'
  };

  const fetchVideos = async (category, subcategory = null) => {
    setLoading(true);
    try {
      const endpoint = subcategory 
        ? `http://localhost:8001/api/videos?category=${category}&subcategory=${subcategory}`
        : `http://localhost:8001/api/videos?category=${category}`;

      const response = await fetch(endpoint);
      const data = await response.json();
      setVideos(data.videos || []);
    } catch (error) {
      console.error('Error fetching videos:', error);
      setVideos([]);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    if (currentView === 'home') {
      fetchVideos(selectedCategory, selectedLanguage);
    }
  }, [selectedCategory, selectedLanguage, currentView]);

  const handleCategorySelect = (category) => {
    setSelectedCategory(category);
    setSelectedLanguage(null);
    setCurrentView('home');
    setLanguagesExpanded(false);
  };

  const handleLanguageSelect = (language) => {
    setSelectedCategory('languages');
    setSelectedLanguage(language);
    setCurrentView('home');
  };

  const handleVideoSelect = (video) => {
    setSelectedVideo(video);
    setCurrentView('player');
  };

  const handleBackToHome = () => {
    setCurrentView('home');
    setSelectedVideo(null);
  };

  const toggleLanguagesExpanded = () => {
    setLanguagesExpanded(!languagesExpanded);
    if (!languagesExpanded) {
      setSelectedCategory('languages');
    }
  };

  const VideoCard = ({ video }) => (
    <div 
      className="bg-[#161B22] rounded-lg overflow-hidden hover:scale-105 hover:shadow-[0_0_20px_rgba(0,255,255,0.3)] transition-all duration-300 cursor-pointer group"
      onClick={() => handleVideoSelect(video)}
    >
      <div className="relative">
        <img 
          src={video.thumbnail} 
          alt={video.title}
          className="w-full h-48 object-cover group-hover:brightness-110 transition-all duration-300"
        />
        <div className="absolute inset-0 bg-black bg-opacity-0 group-hover:bg-opacity-20 transition-all duration-300 flex items-center justify-center">
          <div className="bg-[#00FFFF] rounded-full p-3 opacity-0 group-hover:opacity-100 transition-opacity duration-300 shadow-[0_0_15px_rgba(0,255,255,0.5)]">
            <FaPlay className="w-6 h-6 text-black" />
          </div>
        </div>
      </div>
      <div className="p-4">
        <h3 className="text-[#E0E0E0] font-semibold text-sm mb-2 line-clamp-2 group-hover:text-[#00FFFF] transition-colors duration-300">
          {video.title}
        </h3>
        <p className="text-gray-400 text-xs">{video.channelTitle}</p>
        <p className="text-gray-500 text-xs mt-1">{video.publishedAt}</p>
      </div>
    </div>
  );

  const VideoPlayer = () => (
    <div className="min-h-screen bg-[#0D1117]">
      <div className="max-w-6xl mx-auto px-4 py-8">
        <button 
          onClick={handleBackToHome}
          className="mb-6 flex items-center text-gray-400 hover:text-[#00FFFF] transition-colors duration-300 group"
        >
          <FaChevronRight className="w-4 h-4 mr-2 rotate-180 group-hover:scale-110 transition-transform duration-300" />
          Back to Videos
        </button>

        <div className="bg-[#161B22] rounded-lg overflow-hidden shadow-[0_0_30px_rgba(0,255,255,0.1)]">
          <div className="aspect-video">
            <iframe
              src={`https://www.youtube.com/embed/${selectedVideo.videoId}?rel=0&modestbranding=1`}
              title={selectedVideo.title}
              className="w-full h-full"
              allowFullScreen
            />
          </div>
          <div className="p-6">
            <h1 className="text-2xl font-bold text-[#E0E0E0] mb-4">{selectedVideo.title}</h1>
            <div className="flex items-center justify-between text-gray-400 mb-4">
              <span className="text-[#00FFFF] font-medium">{selectedVideo.channelTitle}</span>
              <span>{selectedVideo.publishedAt}</span>
            </div>
            <div className="text-gray-300 leading-relaxed">
              {selectedVideo.description}
            </div>
          </div>
        </div>
      </div>
    </div>
  );

  const HomeView = () => (
    <div className="min-h-screen bg-[#0D1117] flex">
      {/* Left Sidebar */}
      <aside className="w-64 bg-[#21262D] min-h-screen p-6 flex flex-col">
        {/* Logo/Title */}
        <div className="mb-8">
          <h1 className="text-2xl font-bold text-[#E0E0E0] mb-2">BracketsTV</h1>
          <p className="text-gray-400 text-sm">Curated Interview Prep</p>
        </div>

        {/* Navigation Links */}
        <nav className="flex-1">
          <ul className="space-y-2">
            {Object.entries(categories).map(([key, category]) => {
              const IconComponent = category.icon;
              const isActive = selectedCategory === key;
              const isLanguages = key === 'languages';

              return (
                <li key={key}>
                  <button
                    onClick={isLanguages ? toggleLanguagesExpanded : () => handleCategorySelect(key)}
                    className={`w-full text-left px-4 py-3 rounded-lg transition-all duration-300 flex items-center justify-between group ${
                      isActive 
                        ? 'bg-[#0D1117] text-[#00FFFF] border-l-2 border-[#00FFFF] shadow-[0_0_10px_#00FFFF]' 
                        : 'text-[#E0E0E0] hover:bg-[#2C313A] hover:text-[#00FFFF]'
                    }`}
                  >
                    <div className="flex items-center">
                      <IconComponent className="w-5 h-5 mr-3 group-hover:scale-110 transition-transform duration-300" />
                      <span className="font-medium">{category.name}</span>
                    </div>
                    {isLanguages && (
                      <div className="transition-transform duration-300" style={{ transform: languagesExpanded ? 'rotate(90deg)' : 'rotate(0deg)' }}>
                        <FaChevronRight className="w-4 h-4" />
                      </div>
                    )}
                  </button>

                  {/* Language Subcategories */}
                  {isLanguages && languagesExpanded && (
                    <div className="ml-6 mt-2 space-y-1 animate-in slide-in-from-top-2 duration-300">
                      {Object.entries(languageSubcategories).map(([langKey, langName]) => (
                        <button
                          key={langKey}
                          onClick={() => handleLanguageSelect(langKey)}
                          className={`w-full text-left px-4 py-2 rounded-lg transition-all duration-300 text-sm ${
                            selectedLanguage === langKey
                              ? 'bg-[#0D1117] text-[#00FFFF] border-l-2 border-[#00FFFF] shadow-[0_0_8px_#00FFFF]'
                              : 'text-gray-400 hover:bg-[#2C313A] hover:text-[#00FFFF]'
                          }`}
                        >
                          {langName}
                        </button>
                      ))}
                    </div>
                  )}
                </li>
              );
            })}
          </ul>
        </nav>
      </aside>

      {/* Main Content Area */}
      <main className="flex-1 p-8">
        {/* Category Header */}
        <div className="mb-8">
          <h2 className="text-4xl font-bold text-[#E0E0E0] mb-2">
            {selectedLanguage 
              ? `${languageSubcategories[selectedLanguage]} Tutorials`
              : categories[selectedCategory].name
            }
          </h2>
          <p className="text-gray-400 text-lg">
            {selectedLanguage 
              ? `Master ${languageSubcategories[selectedLanguage]} programming`
              : categories[selectedCategory].description
            }
          </p>
        </div>

        {/* Loading State */}
        {loading && (
          <div className="flex justify-center items-center h-64">
            <div className="relative">
              <div className="w-16 h-16 border-4 border-[#21262D] border-t-[#00FFFF] rounded-full animate-spin"></div>
              <div className="absolute inset-0 w-16 h-16 border-4 border-transparent border-r-[#00FFFF] rounded-full animate-spin" style={{ animationDirection: 'reverse', animationDuration: '1.5s' }}></div>
            </div>
          </div>
        )}

        {/* Video Grid */}
        {!loading && videos.length > 0 && (
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
            {videos.map((video, index) => (
              <VideoCard key={index} video={video} />
            ))}
          </div>
        )}

        {/* Empty State */}
        {!loading && videos.length === 0 && (
          <div className="text-center py-16">
            <div className="w-24 h-24 mx-auto mb-6 bg-[#21262D] rounded-full flex items-center justify-center">
              <FaCode className="w-12 h-12 text-gray-500" />
            </div>
            <h3 className="text-2xl font-semibold text-[#E0E0E0] mb-4">No Videos Available</h3>
            <p className="text-gray-400 text-lg mb-6">
              {selectedLanguage 
                ? `No ${languageSubcategories[selectedLanguage]} videos found at the moment.`
                : 'No videos found for this category. Try selecting a different category.'
              }
            </p>
            <div className="text-sm text-gray-500">
              <p>Videos will appear here once the YouTube API quota resets.</p>
              <p className="mt-1">Check back tomorrow for fresh content!</p>
            </div>
          </div>
        )}
      </main>
    </div>
  );

  return (
    <div className="App">
      {currentView === 'player' && selectedVideo ? <VideoPlayer /> : <HomeView />}
    </div>
  );
};

export default App;
