import React, { useState, useEffect } from 'react';

const App = () => {
  const [currentView, setCurrentView] = useState('home');
  const [selectedCategory, setSelectedCategory] = useState('dsa');
  const [videos, setVideos] = useState([]);
  const [selectedVideo, setSelectedVideo] = useState(null);
  const [loading, setLoading] = useState(false);

  const categories = {
    dsa: { name: 'Data Structures & Algorithms', icon: '🧮' },
    system: { name: 'System Design', icon: '🏗️' },
    behavioral: { name: 'Behavioral Questions', icon: '💼' },
    languages: { name: 'Language-Specific Prep', icon: '💻' }
  };

  const languageSubcategories = {
    python: 'Python',
    javascript: 'JavaScript',
    java: 'Java',
    cpp: 'C++',
    csharp: 'C#',
    go: 'Go',
    rust: 'Rust',
    kotlin: 'Kotlin',
    swift: 'Swift',
    typescript: 'TypeScript'
  };

  const fetchVideos = async (category, subcategory = null) => {
    setLoading(true);
    try {
      const endpoint = subcategory 
        ? `http://localhost:8001/api/videos?category=${category}&subcategory=${subcategory}`
        : `http://localhost:8001/api/videos?category=${category}`;

      const response = await fetch(endpoint);
      const data = await response.json();
      setVideos(data.videos || []);
    } catch (error) {
      console.error('Error fetching videos:', error);
      setVideos([]);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    if (currentView === 'home') {
      fetchVideos(selectedCategory);
    }
  }, [selectedCategory, currentView]);

  const handleCategorySelect = (category) => {
    setSelectedCategory(category);
    setCurrentView('home');
  };

  const handleVideoSelect = (video) => {
    setSelectedVideo(video);
    setCurrentView('player');
  };

  const handleBackToHome = () => {
    setCurrentView('home');
    setSelectedVideo(null);
  };

  const handleLanguageSelect = (language) => {
    setSelectedCategory('languages');
    setCurrentView('home');
    fetchVideos('languages', language);
  };

  const VideoCard = ({ video }) => (
    <div 
      className="bg-gray-800 rounded-lg overflow-hidden hover:bg-gray-700 transition-colors cursor-pointer group"
      onClick={() => handleVideoSelect(video)}
    >
      <div className="relative">
        <img 
          src={video.thumbnail} 
          alt={video.title}
          className="w-full h-48 object-cover group-hover:scale-105 transition-transform duration-200"
        />
        <div className="absolute inset-0 bg-black bg-opacity-0 group-hover:bg-opacity-20 transition-all duration-200 flex items-center justify-center">
          <div className="bg-red-600 rounded-full p-3 opacity-0 group-hover:opacity-100 transition-opacity duration-200">
            <svg className="w-6 h-6 text-white" fill="currentColor" viewBox="0 0 24 24">
              <path d="M8 5v14l11-7z"/>
            </svg>
          </div>
        </div>
      </div>
      <div className="p-4">
        <h3 className="text-white font-semibold text-sm mb-2 line-clamp-2">{video.title}</h3>
        <p className="text-gray-400 text-xs">{video.channelTitle}</p>
        <p className="text-gray-500 text-xs mt-1">{video.publishedAt}</p>
      </div>
    </div>
  );

  const VideoPlayer = () => (
    <div className="min-h-screen bg-gray-900">
      <div className="max-w-6xl mx-auto px-4 py-8">
        <button 
          onClick={handleBackToHome}
          className="mb-6 flex items-center text-gray-400 hover:text-white transition-colors"
        >
          <svg className="w-5 h-5 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
          </svg>
          Back to Videos
        </button>

        <div className="bg-gray-800 rounded-lg overflow-hidden">
          <div className="aspect-video">
            <iframe
              src={`https://www.youtube.com/embed/${selectedVideo.videoId}?rel=0&modestbranding=1`}
              title={selectedVideo.title}
              className="w-full h-full"
              allowFullScreen
            />
          </div>
          <div className="p-6">
            <h1 className="text-2xl font-bold text-white mb-4">{selectedVideo.title}</h1>
            <div className="flex items-center justify-between text-gray-400 mb-4">
              <span>{selectedVideo.channelTitle}</span>
              <span>{selectedVideo.publishedAt}</span>
            </div>
            <div className="text-gray-300 leading-relaxed">
              {selectedVideo.description}
            </div>
          </div>
        </div>
      </div>
    </div>
  );

  const HomeView = () => (
    <div className="min-h-screen bg-gray-900">
      <div className="flex">
        {/* Sidebar */}
        <div className="w-64 bg-gray-800 min-h-screen p-6">
          <div className="mb-8">
            <h1 className="text-2xl font-bold text-white mb-2">BracketsTV</h1>
            <p className="text-gray-400 text-sm">Curated Interview Prep</p>
          </div>

          <nav className="space-y-2">
            {Object.entries(categories).map(([key, category]) => (
              <button
                key={key}
                onClick={() => handleCategorySelect(key)}
                className={`w-full text-left px-4 py-3 rounded-lg transition-colors ${
                  selectedCategory === key 
                    ? 'bg-red-600 text-white' 
                    : 'text-gray-400 hover:bg-gray-700 hover:text-white'
                }`}
              >
                <span className="mr-3">{category.icon}</span>
                {category.name}
              </button>
            ))}
          </nav>

          {selectedCategory === 'languages' && (
            <div className="mt-8">
              <h3 className="text-white font-semibold mb-4">Languages</h3>
              <div className="space-y-1">
                {Object.entries(languageSubcategories).map(([key, name]) => (
                  <button
                    key={key}
                    onClick={() => handleLanguageSelect(key)}
                    className="w-full text-left px-4 py-2 text-sm text-gray-400 hover:bg-gray-700 hover:text-white rounded transition-colors"
                  >
                    {name}
                  </button>
                ))}
              </div>
            </div>
          )}
        </div>

        {/* Main Content */}
        <div className="flex-1 p-8">
          <div className="mb-6">
            <h2 className="text-3xl font-bold text-white mb-2">
              {categories[selectedCategory].name}
            </h2>
            <p className="text-gray-400">Hand-picked videos from trusted channels</p>
          </div>

          {loading ? (
            <div className="flex justify-center items-center h-64">
              <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-red-600"></div>
            </div>
          ) : (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
              {videos.map((video, index) => (
                <VideoCard key={index} video={video} />
              ))}
            </div>
          )}

          {!loading && videos.length === 0 && (
            <div className="text-center py-12">
              <div className="text-gray-400 text-lg">No videos found</div>
              <p className="text-gray-500 mt-2">Try selecting a different category</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );

  return (
    <div className="App">
      {currentView === 'player' && selectedVideo ? <VideoPlayer /> : <HomeView />}
    </div>
  );
};

export default App;
