import React, { useState, useEffect } from 'react';
import { FaCode, FaCogs, FaUserTie, FaCodeBranch, FaChevronDown, FaPlay, FaArrowLeft, FaExternalLinkAlt, FaPython, FaJs, FaJava, FaRust, FaGolang } from 'react-icons/fa';
import { SiCplusplus, SiCsharp, SiKotlin, SiSwift, SiTypescript, SiMysql } from 'react-icons/si';
import './App.css';

// Custom color palette
const colors = {
  'dark-charcoal': '#21262D',
  'near-black': '#0D1117',
  'intermediate-gray': '#161B22',
  'glowing-cyan': '#00FFFF',
  'light-gray': '#E0E0E0'
};

// Categories configuration
const categories = [
  {
    name: 'Data Structures & Algorithms',
    path: 'dsa',
    icon: <FaCode className="w-4 h-4" />,
    description: 'Master fundamental algorithms and data structures'
  },
  {
    name: 'System Design',
    path: 'system',
    icon: <FaCogs className="w-4 h-4" />,
    description: 'Learn to design scalable systems'
  },
  {
    name: 'Behavioral Questions',
    path: 'behavioral',
    icon: <FaUserTie className="w-4 h-4" />,
    description: 'Ace your behavioral interviews'
  },
  {
    name: 'Language-Specific Prep',
    path: 'languages',
    icon: <FaCodeBranch className="w-4 h-4" />,
    description: 'Deep dive into programming languages'
  }
];

// Language configuration for the Language Hub
const languages = [
  { name: 'Python', path: 'python', icon: <FaPython className="w-8 h-8" /> },
  { name: 'JavaScript', path: 'javascript', icon: <FaJs className="w-8 h-8" /> },
  { name: 'Java', path: 'java', icon: <FaJava className="w-8 h-8" /> },
  { name: 'C++', path: 'cpp', icon: <SiCplusplus className="w-8 h-8" /> },
  { name: 'C#', path: 'csharp', icon: <SiCsharp className="w-8 h-8" /> },
  { name: 'Go', path: 'go', icon: <FaGolang className="w-8 h-8" /> },
  { name: 'Rust', path: 'rust', icon: <FaRust className="w-8 h-8" /> },
  { name: 'SQL', path: 'sql', icon: <SiMysql className="w-8 h-8" /> },
  { name: 'Kotlin', path: 'kotlin', icon: <SiKotlin className="w-8 h-8" /> },
  { name: 'Swift', path: 'swift', icon: <SiSwift className="w-8 h-8" /> }
];

function App() {
  const [videos, setVideos] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [selectedVideo, setSelectedVideo] = useState(null);
  const [currentCategory, setCurrentCategory] = useState('dsa');
  const [selectedLanguage, setSelectedLanguage] = useState(null);

  // Fetch videos from API
  const fetchVideos = async (category, subcategory = null) => {
    setLoading(true);
    setError(null);

    try {
      const url = subcategory 
        ? `http://localhost:8001/api/videos?category=${category}&subcategory=${subcategory}`
        : `http://localhost:8001/api/videos?category=${category}`;

      const response = await fetch(url);
      const data = await response.json();

      if (data.videos && data.videos.length > 0) {
        setVideos(data.videos);
      } else {
        setVideos([]);
        setError('No videos found for this category');
      }
    } catch (err) {
      setError('Failed to fetch videos');
      console.error('Error fetching videos:', err);
    } finally {
      setLoading(false);
    }
  };

  // Single navigation handler for all sidebar clicks
  const handleNavClick = (categoryName) => {
    // Handle main categories
    if (['Data Structures & Algorithms', 'System Design', 'Behavioral Questions', 'Language-Specific Prep'].includes(categoryName)) {
      const categoryMap = {
        'Data Structures & Algorithms': 'dsa',
        'System Design': 'system',
        'Behavioral Questions': 'behavioral',
        'Language-Specific Prep': 'languages'
      };

      setCurrentCategory(categoryMap[categoryName]);
      setSelectedLanguage(null); // Clear selected language when switching categories

      // Only fetch videos for main categories (not Language-Specific Prep)
      if (categoryName !== 'Language-Specific Prep') {
        fetchVideos(categoryMap[categoryName]);
      }
    }
  };

  // Handle language selection from Language Hub
  const handleLanguageClick = (languageName) => {
    setSelectedLanguage(languageName);
    fetchVideos('languages', languageName.toLowerCase().replace('c#', 'csharp').replace('c++', 'cpp'));
  };

  // Handle video click
  const handleVideoClick = (video) => {
    setSelectedVideo(video);
  };

  // Handle back to grid
  const handleBackToGrid = () => {
    setSelectedVideo(null);
  };

  // Load initial videos
  useEffect(() => {
    fetchVideos('dsa');
  }, []);

  // Video Player Component
  const VideoPlayer = () => (
    <div className="min-h-screen" style={{ backgroundColor: colors['near-black'], color: colors['light-gray'] }}>
      <div className="p-6">
        <button
          onClick={handleBackToGrid}
          className="flex items-center text-cyan-400 hover:text-cyan-300 transition-colors duration-300 mb-6"
        >
          <FaArrowLeft className="w-4 h-4 mr-2" />
          Back to Videos
        </button>

        <div className="max-w-4xl mx-auto">
          <div className="aspect-video rounded-lg overflow-hidden mb-6" style={{ backgroundColor: colors['intermediate-gray'] }}>
            <iframe
              src={`https://www.youtube.com/embed/${selectedVideo.videoId}`}
              title={selectedVideo.title}
              className="w-full h-full"
              allowFullScreen
            />
          </div>

          <div className="rounded-lg p-6" style={{ backgroundColor: colors['intermediate-gray'] }}>
            <h1 className="text-2xl font-bold mb-4" style={{ color: colors['light-gray'] }}>
              {selectedVideo.title}
            </h1>
            <p className="mb-4" style={{ color: colors['light-gray'], opacity: 0.8 }}>
              Channel: {selectedVideo.channelName}
            </p>
            <p className="text-sm line-clamp-3" style={{ color: colors['light-gray'], opacity: 0.6 }}>
              {selectedVideo.description}
            </p>
            <a
              href={`https://www.youtube.com/watch?v=${selectedVideo.videoId}`}
              target="_blank"
              rel="noopener noreferrer"
              className="inline-flex items-center text-cyan-400 hover:text-cyan-300 transition-colors duration-300 mt-4"
            >
              <FaExternalLinkAlt className="w-4 h-4 mr-2" />
              Watch on YouTube
            </a>
          </div>
        </div>
      </div>
    </div>
  );

  // Main Grid Component
  const VideoGrid = () => (
    <div className="min-h-screen" style={{ backgroundColor: colors['near-black'] }}>
      <div className="flex">
        {/* Sidebar */}
        <aside 
          className="fixed left-0 top-0 h-full w-72 overflow-y-auto"
          style={{ backgroundColor: colors['dark-charcoal'] }}
        >
          <div className="p-6">
            <div className="mb-8">
              <h1 className="text-2xl font-bold mb-2" style={{ color: colors['light-gray'] }}>
                BracketsTV
              </h1>
              <p className="text-sm" style={{ color: colors['light-gray'], opacity: 0.6 }}>
                Curated Interview Prep
              </p>
            </div>

            <nav className="space-y-2">
              {categories.map((category) => (
                <div key={category.name}>
                  {category.subcategories.length > 0 ? (
                    <div>
                      <button
                        onClick={() => handleNavClick(category.name)}
                        className={`w-full flex items-center justify-between p-3 rounded-lg transition-colors duration-300 border-l-2
                          ${currentCategory === category.name || expandedAccordion === category.name
                            ? 'border-cyan-400 shadow-[0_0_10px_#00FFFF]'
                            : 'border-transparent hover:transition-colors duration-300'
                          }`}
                        style={{
                          backgroundColor: currentCategory === category.name || expandedAccordion === category.name ? colors['near-black'] : 'transparent',
                          color: currentCategory === category.name || expandedAccordion === category.name ? colors['glowing-cyan'] : colors['light-gray']
                        }}
                        onMouseEnter={(e) => {
                          if (currentCategory !== category.name && expandedAccordion !== category.name) {
                            e.target.style.backgroundColor = colors['intermediate-gray'];
                            e.target.style.color = colors['glowing-cyan'];
                          }
                        }}
                        onMouseLeave={(e) => {
                          if (currentCategory !== category.name && expandedAccordion !== category.name) {
                            e.target.style.backgroundColor = 'transparent';
                            e.target.style.color = colors['light-gray'];
                          }
                        }}
                      >
                        <div className="flex items-center">
                          {category.icon}
                          <span className="ml-3">{category.name}</span>
                        </div>
                        <FaChevronDown 
                          className={`w-4 h-4 transition-transform duration-300
                            ${expandedAccordion === category.name ? 'rotate-180' : ''}
                          `} 
                        />
                      </button>

                      {expandedAccordion === category.name && (
                        <div className="ml-6 mt-2 space-y-1">
                          {category.subcategories.map((subcategory) => (
                            <button
                              key={subcategory.name}
                              onClick={() => handleNavClick(subcategory.name)}
                              className={`w-full text-left p-2 rounded-lg transition-colors duration-300 border-l-2
                                ${currentCategory === `languages-${subcategory.path}`
                                  ? 'border-cyan-400 shadow-[0_0_10px_#00FFFF]'
                                  : 'border-transparent hover:transition-colors duration-300'
                                }`}
                              style={{
                                backgroundColor: currentCategory === `languages-${subcategory.path}` ? colors['near-black'] : 'transparent',
                                color: currentCategory === `languages-${subcategory.path}` ? colors['glowing-cyan'] : colors['light-gray']
                              }}
                              onMouseEnter={(e) => {
                                if (currentCategory !== `languages-${subcategory.path}`) {
                                  e.target.style.backgroundColor = colors['intermediate-gray'];
                                  e.target.style.color = colors['glowing-cyan'];
                                }
                              }}
                              onMouseLeave={(e) => {
                                if (currentCategory !== `languages-${subcategory.path}`) {
                                  e.target.style.backgroundColor = 'transparent';
                                  e.target.style.color = colors['light-gray'];
                                }
                              }}
                            >
                              {subcategory.name}
                            </button>
                          ))}
                        </div>
                      )}
                    </div>
                  ) : (
                    <button
                      onClick={() => handleNavClick(category.name)}
                      className={`w-full flex items-center p-3 rounded-lg transition-colors duration-300 border-l-2
                        ${currentCategory === category.path
                          ? 'border-cyan-400 shadow-[0_0_10px_#00FFFF]'
                          : 'border-transparent hover:transition-colors duration-300'
                        }`}
                      style={{
                        backgroundColor: currentCategory === category.path ? colors['near-black'] : 'transparent',
                        color: currentCategory === category.path ? colors['glowing-cyan'] : colors['light-gray']
                      }}
                      onMouseEnter={(e) => {
                        if (currentCategory !== category.path) {
                          e.target.style.backgroundColor = colors['intermediate-gray'];
                          e.target.style.color = colors['glowing-cyan'];
                        }
                      }}
                      onMouseLeave={(e) => {
                        if (currentCategory !== category.path) {
                          e.target.style.backgroundColor = 'transparent';
                          e.target.style.color = colors['light-gray'];
                        }
                      }}
                    >
                      {category.icon}
                      <span className="ml-3">{category.name}</span>
                    </button>
                  )}
                </div>
              ))}
            </nav>
          </div>
        </aside>

        {/* Main Content */}
        <main className="flex-1 ml-72 min-h-screen" style={{ backgroundColor: colors['near-black'] }}>
          <div className="p-8">
            <div className="mb-8">
              <h2 className="text-4xl font-bold mb-2" style={{ color: colors['light-gray'] }}>
                {currentCategory === 'Language-Specific Prep' 
                  ? 'Select a Language'
                  : categories.find(cat => currentCategory.startsWith(cat.path))?.name || 'Videos'
                }
              </h2>
              <p className="text-lg" style={{ color: colors['light-gray'], opacity: 0.6 }}>
                {currentCategory === 'Language-Specific Prep'
                  ? 'Choose a language from the list on the left to get started.'
                  : categories.find(cat => currentCategory.startsWith(cat.path))?.description || 'Curated video content'
                }
              </p>
            </div>

            {loading && (
              <div className="flex justify-center items-center h-64">
                <div className="animate-spin rounded-full h-12 w-12 border-b-2" style={{ borderColor: colors['glowing-cyan'] }}></div>
              </div>
            )}

            {currentCategory === 'Language-Specific Prep' && (
              <div className="text-center py-16">
                <div className="w-24 h-24 mx-auto mb-6 rounded-full flex items-center justify-center" style={{ backgroundColor: colors['dark-charcoal'] }}>
                  <FaCode className="w-12 h-12" style={{ color: colors['glowing-cyan'] }} />
                </div>
                <h3 className="text-2xl font-semibold mb-4" style={{ color: colors['light-gray'] }}>Select a Language</h3>
                <p className="text-lg mb-6" style={{ color: colors['light-gray'], opacity: 0.6 }}>
                  Choose a language from the list on the left to get started.
                </p>
              </div>
            )}

            {error && (
              <div className="text-center py-12">
                <p className="text-lg" style={{ color: colors['light-gray'], opacity: 0.6 }}>{error}</p>
              </div>
            )}

            {!loading && !error && videos.length > 0 && (
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
                {videos.map((video) => (
                  <div
                    key={video.videoId}
                    onClick={() => handleVideoClick(video)}
                    className="rounded-lg overflow-hidden cursor-pointer transition-all duration-300 hover:scale-105"
                    style={{ backgroundColor: colors['intermediate-gray'] }}
                    onMouseEnter={(e) => {
                      e.target.style.boxShadow = '0 0 20px rgba(0,255,255,0.3)';
                    }}
                    onMouseLeave={(e) => {
                      e.target.style.boxShadow = 'none';
                    }}
                  >
                    <div className="aspect-video relative" style={{ backgroundColor: '#374151' }}>
                      <img
                        src={video.thumbnail}
                        alt={video.title}
                        className="w-full h-full object-cover"
                      />
                      <div className="absolute inset-0 flex items-center justify-center bg-black/20 opacity-0 hover:opacity-100 transition-opacity duration-300">
                        <FaPlay className="w-8 h-8 text-white" />
                      </div>
                    </div>
                    <div className="p-4">
                      <h3 className="font-semibold line-clamp-2 mb-2" style={{ color: colors['light-gray'] }}>
                        {video.title}
                      </h3>
                      <p className="text-sm" style={{ color: colors['light-gray'], opacity: 0.6 }}>
                        {video.channelName}
                      </p>
                    </div>
                  </div>
                ))}
              </div>
            )}

            {!loading && !error && videos.length === 0 && (
              <div className="text-center py-16">
                <div className="w-24 h-24 mx-auto mb-6 rounded-full flex items-center justify-center" style={{ backgroundColor: colors['dark-charcoal'] }}>
                  <FaCode className="w-12 h-12" style={{ color: colors['light-gray'], opacity: 0.5 }} />
                </div>
                <h3 className="text-2xl font-semibold mb-4" style={{ color: colors['light-gray'] }}>No Videos Available</h3>
                <p className="text-lg mb-6" style={{ color: colors['light-gray'], opacity: 0.6 }}>
                  No videos found for this category. Try selecting a different category.
                </p>
                <div className="text-sm" style={{ color: colors['light-gray'], opacity: 0.5 }}>
                  <p>Videos will appear here once the YouTube API quota resets.</p>
                  <p className="mt-1">Check back tomorrow for fresh content!</p>
                </div>
              </div>
            )}
          </div>
        </main>
      </div>
    </div>
  );

  return selectedVideo ? <VideoPlayer /> : <VideoGrid />;
}

export default App;
